import { async } from '@firebase/util';
import axios from "axios";
import { useEffect, useReducer, useState } from 'react';
import { useParams } from "react-router-dom";
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Container from 'react-bootstrap/Container';

const reducer = (state, action) => {
    switch (action.type) {
        case 'FETCH_REQUEST':
            return { ...state, loading: true };
        case 'FETCH_SUCCESS':
            //showing only one product so products changes to product
            return { ...state, product: action.payload, loading: false };
        case 'FETCH FAIL':
            return { ...state, loading: false, error: action.payload };
        default:
            return state;
    }
}


export default function ProductScreen() {
    const params = useParams()
    const { slug } = params

    const [{ loading, error, product }, dispatch] = useReducer(reducer, {
        //show only one product
        product: [],
        loading: true,
        error: '',
    });
    useEffect(() => {
        const fetchData = async () => {
            dispatch({ type: 'FETCH_REQUEST' });
            try {
                const result = await axios.get(`/home/products/${slug}`);
                dispatch({ type: 'FETCH_SUCCESS', payload: result.data })
            } catch (err) {
                dispatch({ type: 'FETCH_FAIL', payload: err.message });
            }
        };
        fetchData();
    }, [slug]);

    return loading ? (
        <div>Loading...</div>
    ) : error ? (
        <div>{error}</div>
    ) : (
        <Container className = "itemDescription">
            <Row>
                <Col>PRODUCT CATEGORIES</Col>
                <Col>Annual Flowers</Col>
                <Col>Garden Plants & Flowers</Col>
                <Col>Garden SuppRowes & Plant Care</Col>
            </Row>
            <Row>
                <img className="detailPic" src={product.image} alt={product.name} />
            </Row>
            <Row>
                <Col>{product.name}</Col>
                <Col>{product.description}</Col>
            </Row>
        </Container>
    );
}
