import React, { useState } from 'react';
import {useHistory, useNavigate} from 'react-router-dom';


export default function Form() {

    const [form, setForm] = useState({
        name: "",
        phone: "",
        email: "",
        comment: "",
        projectAddress: "",
    })

    const [count, setCount] = useState(1)

    const updateForm = (e) => {
        setForm({
            ...form,
            [e.target.name]: e.target.value,
        })
        // console.log(e.target)
        // console.log(form)
    }


    //onSubmit redirect to a new page
    //use history.push. import from react-router-dom
    const history = useNavigate();


    //"send" method using fetch
    const sendEmail = (e) => {

        e.preventDefault();

        const data = {
            service_id: 'service_z3p8h0m',
            template_id: 'template_svmf3nm',
            user_id: 'user_7Pf1rN0FgZQwrrMpFSw55',
            template_params: form
        };


        fetch('https://api.emailjs.com/api/v1.0/email/send', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                'Accept': 'application/json',
            },
            body: JSON.stringify(data)
        })

            .then((result) => {
                console.log(result.statusText);
            }, (error) => {
                console.log(error.statusText);
            });

        e.target.reset();

        history.push("/portfolio")     //onSubmit redirect to a new page
    }



    return (
        <div className="form-box">


            <form onSubmit={sendEmail}>

                 (  //when the page count is 1 show this 
                    <div className="field1">
                        <label> customer info </label>

                        <input
                            type="text"
                            className="form-input"
                            name="name"
                            placeholder="Name"
                            onChange={updateForm}
                            value={form.name}
                        />


                        <input
                            type="tel"
                            className="form-input"
                            name="phone"
                            placeholder="Phone 000-000-0000"
                            onChange={updateForm}
                            value={form.phone}
                        />

                        <input
                            type="email"
                            className="form-input"
                            name="email"
                            placeholder="E-mail"
                            onChange={updateForm}
                            value={form.email}
                        />

                        <textarea
                            type="text"
                            className="form-input"
                            name="comment"
                            placeholder="comment"
                            onChange={updateForm}
                            value={form.comment}
                        />

                        <textarea
                            type="text"
                            className="form-input"
                            name="projectAddress"
                            placeholder="Physical location of the project"
                            onChange={updateForm}
                            value={form.projectAddress}
                        />
                    </div>
                ) : null} 

                {/* SUBMIT button */}
                {count === 4 ? (
                    <button
                        type="submit"
                        id="submitBtn"
                        className="submitBtn"
                    > submit</button>
                ) : null}

            </form>
            {/* end of form */}


            {/* PREV button */}
            <button
                className="prevBtn"
                type="submit"
                onClick={() => setCount(count - 1)} //decrease the page count
                disabled={count <= 1} //don't do anytying when the page is the first page
            > PREV </button>

        </div>
        // end of form-box
    )
}
