import axios from 'axios';
import Product from '../components/Product';
import { useEffect, useReducer,  } from 'react';
import { useParams } from 'react-router-dom';
import Row from 'react-bootstrap/esm/Row';


const reducer = (state, action) => {
    switch (action.type) {
        case 'FETCH_REQUEST':
            return { ...state, loading: true };
        case 'FETCH_SUCCESS':
            //showing only the products in the category
            return { ...state, products: action.payload, loading: false };
        case 'FETCH FAIL':
            return { ...state, loading: false, error: action.payload };
        default:
            return state;
    }
}


export default function CategoryScreen() {
        const params = useParams()
        const { category } = params

        const [{ loading, error, products }, dispatch] = useReducer(reducer, {
            products: [],
            loading: true,
            error: '',
        });
    useEffect(() => {
        const fetchData = async () => {
            dispatch({ type: 'FETCH_REQUEST' });
            try {
                const result = await axios.get(`/home/products/${category}`);
                dispatch({ type: 'FETCH_SUCCESS', payload: result.data })
                console.log(result)
            } catch (err) {
                dispatch({ type: 'FETCH_FAIL', payload: err.message });
            }

        };
        fetchData();
    }, []);
    return (
        <Container fluid className="itemDescription">
            <Row className="traversal" >Home / {product.category}</Row>
            <Row className="list">
                <h2 className="category">PRODUCT CATEGORIES</h2>
                <Col className="items">Annual Flowers</Col>
                <Col className="items">Garden Plants & Flowers</Col>
                <Col className="items">Garden Supplies & Plant Care</Col>
            </Row>
            <Row className="pic">
                <img className="items" src={product.image} alt={product.name} />
            </Row>
            <Row className="listTwo">
                <h1 className='name'>{product.name}</h1>
                <div className="description">{product.description}</div>
                <div className="productPrice">{product.price}</div>
                <h3>Select Pick-up Location</h3>
                <li>
                    <ul>West Bloomfield</ul>
                    <ul>Clinton Township</ul>
                    <ul>Royal Oak</ul>
                    <ul>Dearborn Heights</ul>
                    <h3>
                        <Button>Add to Cart</Button>
                    </h3>
                </li>

            </Row>
        </Container>
    );
}
