import { async } from '@firebase/util';
import axios from "axios";
import { useEffect, useReducer, useState } from 'react';
import { useParams } from "react-router-dom";


const reducer = (state, action) => {
    switch (action.type) {
        case 'FETCH_REQUEST':
            return { ...state, loading: true };
        case 'FETCH_SUCCESS':
            //showing only one product so products changes to product
            return { ...state, product: action.payload, loading: false };
        case 'FETCH FAIL':
            return { ...state, loading: false, error: action.payload };
        default:
            return state;
    }
}


export default function ProductScreen() {
    const params = useParams()
    const { slug } = params

    const [{ loading, error, product }, dispatch] = useReducer(reducer, {
        //show only one product
        product: [],
        loading: true,
        error: '',
    });
    useEffect(() => {
        const fetchData = async () => {
            dispatch({ type: 'FETCH_REQUEST' });
            try {
                const result = await axios.get(`/home/products/${slug}`);
                dispatch({ type: 'FETCH_SUCCESS', payload: result.data })
            } catch (err) {
                dispatch({ type: 'FETCH_FAIL', payload: err.message });
            }
        };
        fetchData();
    }, [slug]);

    return loading ? (
        <div>Loading...</div>
    ) : error ? (
        <div>{error}</div>
    ) : (
        <div class="row">
            <div class="column">
                <ul> PRODUCT CATEGORIES
                    <li>Annual Flowers</li>
                    <li>Garden Plants & Flowers</li>
                    <li>Garden Supplies & Plant Care</li>
                </ul>
            </div>
            <div class="column">
                <img className="medium" src={product.image} alt={product.name} />
            </div>
            <div class="column">{product.name}{product.description}</div>
        </div>

    );
}
