import { async } from '@firebase/util';
import axios from "axios";
import { useEffect, useReducer, useState } from 'react';
import { useParams } from "react-router-dom";
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Button from 'react-bootstrap/Button';
import Container from 'react-bootstrap/Container';

const reducer = (state, action) => {
    switch (action.type) {
        case 'FETCH_REQUEST':
            return { ...state, loading: true };
        case 'FETCH_SUCCESS':
            //showing only one product so products changes to product
            return { ...state, product: action.payload, loading: false };
        case 'FETCH FAIL':
            return { ...state, loading: false, error: action.payload };
        default:
            return state;
    }
}


export default function ProductScreen() {
    const params = useParams()
    const { category, slug } = params

    const [{ loading, error, product }, dispatch] = useReducer(reducer, {
        //show only one product
        product: [],
        loading: true,
        error: '',
    });
    useEffect(() => {
        const fetchData = async () => {
            dispatch({ type: 'FETCH_REQUEST' });
            try {
                const result = await axios.get(`/home/products/${category}/${slug}`);
                dispatch({ type: 'FETCH_SUCCESS', payload: result.data })
            } catch (err) {
                dispatch({ type: 'FETCH_FAIL', payload: err.message });
            }
        };
        fetchData();
    }, [slug]);

    return loading ? (
        <div>Loading...</div>
    ) : error ? (
        <div>{error}</div>
    ) : (
        <Container fluid className="itemDescription">
            <Row className="traversal" >Home / {product.category}</Row>
            <Row className="list">
                <h2 className="category">PRODUCT CATEGORIES</h2>
                <Col className="items">Annual Flowers</Col>
                <Col className="items">Garden Plants & Flowers</Col>
                <Col className="items">Garden Supplies & Plant Care</Col>
            </Row>
            <Row className="pic">
                <img className="detailPic" src={product.image} alt={product.name} className="items" />
            </Row>
            <Row className="listTwo">
                <h1 className='name'>{product.name}</h1>
                <div className="description">{product.description}</div>
                <div className="productPrice">{product.price}</div>
                <h3>Select Pick-up Location</h3>
                <li>
                    <ul>West Bloomfield</ul>
                    <ul>Clinton Township</ul>
                    <ul>Royal Oak</ul>
                    <ul>Dearborn Heights</ul>
                    <h3>
                        <Button>Add to Cart</Button>
                    </h3>
                </li>

            </Row>
        </Container>
    );
}
