import { async } from '@firebase/util';
import axios from 'axios';
import { useEffect, useReducer, useState } from 'react';
import Product from '../components/Product';
import logger from 'use-reducer-logger';
import { useParams } from 'react-router-dom';
//import data from '../data';

//state = current state and action will be the new state
const reducer = (state, action) => {
    //comparing each action type accordingly
    switch (action.type) {
        //what to return from ajax request
        case 'FETCH_REQUEST':
            return { ...state, loading: true };
        case 'FETCH_SUCCESS':
            return { ...state, products: action.payload, loading: false };
        case 'FETCH FAIL':
            return { ...state, loading: false, error: action.payload };
        default:
            return state;
    }
}

export default function CategoryScreen() {
    const params = useParams()
    const { category } = params

    const [{ loading, error, products }, dispatch] = useReducer(logger(reducer), {
        products: [],
        loading: true,
        error: '',
    });

    useEffect(() => {
        const fetchData = async () => {
            dispatch({ type: 'FETCH_REQUEST' });
            try {
                const result = await axios.get(`/home/products/${category}`);
                dispatch({ type: 'FETCH_SUCCESS', payload: result.data })
            } catch (err) {
                dispatch({ type: 'FETCH_FAIL', payload: err.message });
            }
            //setProducts(result.data);
        };
        fetchData();
    }, []);
    return (
        <div className="container">
            <div className="HomeScreenMini"><p>Category Products</p></div>
            {
                loading ? (
                    <div>Loading...</div>
                ) : error ? (
                    <div>{error}</div>
                ) : (
                    products.map((product) => (
                        <Product key={product._id} product={product}></Product>
                    )))}
        </div>
    )
}
